ec.product.timingBuy = function () {
    var getSku = ec.product.getSkuInfo(ec.product.getSku() || ec.product.defaultSku);
    var _now = ec.product.getSysDate();
    var start = getSku.startTime;
    var startTime = new Date;
    startTime.setTime(start.parseDate("yyyy-MM-dd HH:mm:ss"));
    var startArray = [];
    startArray.push(start);
    var countdownLength = getSku.countDown;
    $("#pro-operation").css("visibility", "visible");
    $("#product-operation").show();
    if (_now - startTime >= 0) {
        var end = getSku.endTime;
        var endTime = new Date;
        if (null == end || "" == end) {
            endTime = new Date(2999, 1, 1, 0, 0, 0)
        } else {
            endTime.setTime(end.parseDate("yyyy-MM-dd HH:mm:ss"))
        }
        if (_now - endTime < 0) {
            var haveQuantity = ec.product.inventory.haveInventory(getSku.id);
            if (haveQuantity) {
                var content = '<a href="javascript:;" onclick="ec.product.addCart()" class="product-button01" ><span></span></a>' + '<a href="javascript:;" onclick="ec.product.orderNow()" class="product-button02" ><span></span></a>' + '<span class="product-button-tips"></span>';
                ec.product.renderVisitorButton($("#pro-operation"));
                $("#pro-operation").html(content);
                $("#product-operation").show()
            } else {
                $("#pro-quantity-area").hide();
                $("#pro-operation").html('<a href="javascript:;" class="product-button02" onclick="ec.product.arrival()"><span></span></a>');
                $("#product-operation").show();
                $("#product-recommend-all").hide()
            }
        } else {
            $("#pro-operation").html('<a href="javascript:;" class="product-button02 disabled"></a>');
            $("#pro-operation").css("visibility", "visible");
            $("#product-operation").show();
            $("#product-recommend-all").hide()
        }
    } else if (!ec.util.isEmpty(getSku.gotoUrl) && (startTime - _now) / (1e3 * 3600) > countdownLength) {
        if (1 == getSku.isShowReminder) {
            $("#pro-operation").html('<a target="_blank" href="' + getSku.gotoUrl + '" class="product-button02" ><span></span></a>');
            $("#product-operation").show()
        } else if (0 == getSku.isShowReminder) {
            var content = '<a href="javascript:;" class="product-button01 disabled" ><span></span></a>' + '<a href="javascript:;"  class="product-button02 disabled" ><span></span></a><div class="product-time"></div>';
            $("#pro-operation").html(content);
            $("#product-operation").show();
            ec.ui.countdown2($(".product-time"), {
                html: "<p></p><ul><li><span>{#day}</span></li><li><em></em></li><li><span>{#hours}</span></li><li><em>:</em></li><li><span>{#minutes}</span></li><li><em>:</em></li><li><span>{#seconds}</span></li>",
                now: _now,
                times: startArray,
                callback: function (json) {
                    var content = '<a href="javascript:;" onclick="ec.product.addCart()" class="product-button01" ><span></span></a>' + '<a href="javascript:;" onclick="ec.product.orderNow()" class="product-button02" ><span></span></a>';
                    $("#pro-operation").html(content);
                    $("#product-operation").show();
                    ec.product.gift.updateOperationBtns()
                }
            })
        }
    } else {
        var content = '<a href="javascript:;" class="product-button01 disabled" ><span></span></a>' + '<a href="javascript:;"  class="product-button02 disabled" ><span></span></a><div class="product-time"></div>';
        $("#pro-operation").html(content);
        $("#product-operation").show();
        ec.ui.countdown2($(".product-time"), {
            html: "<p></p><ul><li><span>{#day}</span></li><li><em></em></li><li><span>{#hours}</span></li><li><em>:</em></li><li><span>{#minutes}</span></li><li><em>:</em></li><li><span>{#seconds}</span></li>",
            now: _now,
            times: startArray,
            callback: function (json) {
                var content = '<a href="javascript:;" onclick="ec.product.addCart()" class="product-button01" ><span></span></a>' + '<a href="javascript:;" onclick="ec.product.orderNow()" class="product-button02" ><span></span></a>';
                $("#pro-operation").html(content);
                $("#product-operation").show();
                ec.product.showSbomInterest(ec.product.getSkuInfo(ec.product.getSku()));
                ec.product.gift.updateOperationBtns()
            }
        })
    }
};
