function timestampToTime(b) {
    var a = new Date(b);
    Y = a.getFullYear() + ".";
    M = (a.getMonth() + 1 < 10 ? "0" + (a.getMonth() + 1) : a.getMonth() + 1) + ".";
    D = (a.getDate() < 10 ? "0" + a.getDate() : a.getDate()) + " ";
    h = (a.getHours() < 10 ? "0" + a.getHours() : a.getHours()) + ":";
    m = (a.getMinutes() < 10 ? "0" + a.getMinutes() : a.getMinutes());
    return Y + M + D + h + m
}

